{
    if (l1 > r1 || l2 > r2) return 0;
    int root = a[l1];
    int k = mp[root];
    int sum = k - l2;
    node[root].first = dfs(l1 + 1, l1 + sum, l2, k - 1);
    node[root].second = dfs(l1 + sum + 1, r1, k + 1, r2);
